<form id="wpforms-form-71" action="/" class="grid gap-4 my-12">
    <input type="text" name="name" id="elpico_1" placeholder="Full Name" class="py-3 px-4 rounded-sm uppercase placeholder:text-sage/50 text-lavender focus-within:outline-sage focus-within:placeholder:text-transparent">
    <input type="email" name="email" id="elpico_2" placeholder="Email Address" class="py-3 px-4 rounded-sm uppercase placeholder:text-sage text-lavender focus-within:outline-sage focus-within:placeholder:text-transparent">
    <input type="text" name="subject" id="elpico_3" placeholder="Subject" class="py-3 px-4 rounded-sm uppercase placeholder:text-sage text-lavender focus-within:outline-sage focus-within:placeholder:text-transparent">
    <textarea name="" id="elpico_4" name="wpforms[fields][4]" aria-errormessage="elpico_4-error" rows="8" class="py-3 px-4 rounded-sm uppercase placeholder:text-sage text-lavender focus-within:outline-sage focus-within:placeholder:text-transparent" placeholder="Comment or Message"></textarea>
    <input type="submit" value="Send" class="btn btn-inv ml-auto">
</form>